function [I,dI]=inet_mom(m_B,m_C,L,H,Ph,Vh,mu)
    I=[(m_B*(H^2+L^2))/12+m_C*Ph(2,1)*Ph(2,1)*(mu-1)*(mu-1)+m_B*mu*Ph(2,1)*Ph(2,1)*mu,-m_C*Ph(2,1)*Ph(1,1)*(mu-1)*(mu-1)-m_B*mu*Ph(2,1)*Ph(1,1)*mu,0;...
       -m_C*Ph(1,1)*Ph(2,1)*(mu-1)*(mu-1)-m_B*mu*Ph(1,1)*Ph(2,1)*mu,(m_B*(H^2+L^2))/12+m_C*Ph(1,1)*Ph(1,1)*(mu-1)*(mu-1)+m_B*mu*Ph(1,1)*Ph(1,1)*mu,0;...
       0,0,(m_B*L^2)/6+m_B*(mu*Ph(1,1)*Ph(1,1)*mu+mu*Ph(2,1)*Ph(2,1)*mu)+m_C*(Ph(1,1)*Ph(1,1)*(mu-1)*(mu-1)+Ph(2,1)*Ph(2,1)*(mu-1)*(mu-1))];
    dI=[m_C*Vh(2,1)*Ph(2,1)*(mu-1)^2+m_C*Ph(2,1)*(mu-1)^2*Vh(2,1)+m_B*mu*Vh(2,1)*Ph(2,1)*mu+m_B*mu*Ph(2,1)*mu*Vh(2,1),-m_C*Vh(2,1)*Ph(1,1)*(mu-1)^2-m_C*Ph(2,1)*(mu-1)^2*Vh(1,1)-m_B*mu*Vh(2,1)*Ph(1,1)*mu-m_B*mu*Ph(2,1)*mu*Vh(1,1),0;...
        -m_C*Vh(1,1)*Ph(2,1)*(mu-1)^2-m_C*Ph(1,1)*(mu-1)^2*Vh(2,1)-m_B*mu*Vh(1,1)*Ph(2,1)*mu-m_B*mu*Ph(1,1)*mu*Vh(2,1),m_C*Vh(1,1)*Ph(1,1)*(mu-1)^2+m_C*Ph(1,1)*(mu-1)^2*Vh(1,1)+m_B*mu*Vh(1,1)*Ph(1,1)*mu+m_B*mu*Ph(1,1)*mu*Vh(1,1),0;...
        0,0,m_C*(Vh(1,1)*Ph(1,1)*(mu-1)^2+Ph(1,1)*(mu-1)^2*Vh(1,1)+Vh(2,1)*Ph(2,1)*(mu-1)^2+Ph(2,1)*(mu-1)^2*Vh(2,1))+m_B*(mu*Vh(1,1)*Ph(1,1)*mu+mu*Ph(1,1)*mu*Vh(1,1)+mu*Vh(2,1)*Ph(2,1)*mu+mu*Ph(2,1)*mu*Vh(2,1))];
end